import axios from "axios";
import { useEffect, useState } from "react";
import { useNavigate, useParams } from "react-router-dom";
import { Header, StatusChart, StatusChartUser } from "../components";

const UserPage = () => {
    const {user} = useParams();
    const [userData, setUserData] = useState(null);
    const [userObject, setUserObject] = useState(null);
    const [chartData, setChartData] = useState([]);
    const navigate = useNavigate();

    const fetchUser = async () => {
        const response = await axios.get("http://localhost:8080/api/general/user/" + encodeURIComponent(user));
        return response.data;
    }

    useEffect(() => {
        fetchUser().then(data => setUserData(data))
    }, [])

    useEffect(() => {
        if (userData === null) return;
        
        let obj = {}

        try {
            obj.timeSpent = (userData.filter((item) => item.status === "online" || item.status === "dnd" || item.status === "idle").length / 60).toFixed(1);
            obj.lastSeenTimestamp = new Date(userData.filter((item) => item.status === "online" || item.status === "dnd" || item.status === "idle")[0].timestamp).toLocaleString("cs")
            obj.lastSeenStatus = userData.filter((item) => item.status === "online" || item.status === "dnd" || item.status === "idle")[0].status
        } catch {}

        setUserObject(obj)
    }, [userData])

    useEffect(() => {
        console.log(user)
        if (userData === null) return;
        
        let obj = [];

        userData.forEach((item) => {
            obj.push({username: 0.5, timestamp: new Date(item.timestamp).getTime(), status: item.status})
        })

       setChartData(obj)
    }, [userData])

    useEffect(() => {
        const handleKeyDown = (e) => {
            if (e.key === 'Escape') {
                navigate(-1)
            }
        };

        window.addEventListener('keydown', handleKeyDown);

        return () => {
            window.removeEventListener('keydown', handleKeyDown);
        };
    }, []);

    if (userObject === null) return;

    return (
        <>
            <Header />
            <div className="exit-button" onClick={() => navigate(-1)}><div>â¨‰</div><p>ESC</p></div>

            <div className="container">
                <select defaultValue={"day"} onChange={(e) => setSelectedTimeSpan(e.target.value)}>
                    <option value={"hour"}>1H</option>
                    <option value={"day"}>1D</option>
                    <option value={"week"}>1W</option>
                    <option value={"month"}>1M</option>
                    <option value={"year"}>1Y</option>
                </select>

                <div className="user">
                    <div className="user-avatar">
                        <img src={userData[0].avatar}></img>
                    </div>
                    <div className="user-info">
                        <h2>{user}</h2>
                        <hr/>
                        <p>Total time: <span className='badge'>{userObject.timeSpent} h</span></p>
                        <p>Last seen: {userObject.lastSeenTimestamp} ({userObject.lastSeenStatus})</p>

                        <StatusChartUser chartData={chartData} />
                    </div>
                </div>

            </div>
            
        </>
    )
}

export default UserPage;
