import { useEffect, useState, useRef } from 'react';
import axios from "axios";
import '../App.css'
import * as d3 from 'd3';

const HomePage = () => {
    const [data, setData] = useState([]);
    const [users, setUsers] = useState([]);
    const [stats, setStats] = useState(null);

    const [selected, setSelected] = useState("all");

    const fetchData = async () => {
        const response = await axios.get(`http://localhost:8080/api/general/`);
        return response.data;
    }

    const fetchUsers = async () => {
        const response = await axios.get(`http://localhost:8080/api/general/users`);
        return response.data;
    }

    const fetchStats = async () => {
        const response = await axios.get(`http://localhost:8080/api/general/stats`);
        return response.data;
    }

    const svgRef = useRef();

  useEffect(() => {
    // Convert timestamp to Date object for better plotting
    data.forEach(d => {
      d.timestamp = new Date(d.timestamp);
    });

    // Sort by timestamp for the plot
    data.sort((a, b) => a.timestamp - b.timestamp);

    // Define a color scale for the statuses
    const statusColors = d3.scaleOrdinal()
      .domain(['online', 'offline', 'idle', 'dnd'])
      .range(['green', 'grey', 'orange', 'red']);

    // Set up SVG dimensions
    const margin = { top: 20, right: 30, bottom: 50, left: 60 };
    const width = 800 - margin.left - margin.right;
    const height = 400 - margin.top - margin.bottom;

    // Create SVG element
    const svg = d3.select(svgRef.current)
      .attr('width', width + margin.left + margin.right)
      .attr('height', height + margin.top + margin.bottom)
      .append('g')
      .attr('transform', `translate(${margin.left},${margin.top})`);

    // Create scales
    const xScale = d3.scaleTime().domain(d3.extent(data, d => d.timestamp)).range([0, width]);
    const yScale = d3.scaleBand().domain(data.map(d => d.username)).range([height, 0]).padding(0.1);

    // Create axes
    const xAxis = d3.axisBottom(xScale);
    const yAxis = d3.axisLeft(yScale);

    // Add axes to the SVG
    svg.append('g').attr('transform', `translate(0, ${height})`).call(xAxis);
    svg.append('g').call(yAxis);

    // Create scatter plot points
    svg.selectAll('circle')
      .data(data)
      .enter()
      .append('circle')
      .attr('cx', d => xScale(d.timestamp))
      .attr('cy', d => yScale(d.username))
      .attr('r', 5)
      .attr('fill', d => statusColors(d.status));

    // Add legend
    const legend = svg.selectAll('.legend')
      .data(statusColors.domain())
      .enter()
      .append('g')
      .attr('class', 'legend')
      .attr('transform', (d, i) => `translate(0,${i * 20})`);

    legend.append('rect')
      .attr('x', width + 18)
      .attr('width', 18)
      .attr('height', 18)
      .style('fill', statusColors);

    legend.append('text')
      .attr('x', width + 40)
      .attr('y', 9)
      .attr('dy', '.35em')
      .style('text-anchor', 'start')
      .text(d => d);
  }, [data]);

    useEffect(() => {
        fetchData().then(response => setData(response))
        fetchUsers().then(response => setUsers(response))
        fetchStats().then(response => setStats(response))
    }, [])

    return (
        <div className='main'>
            
            <div className='header'>
                <img src={require("../img/discord.png")}></img>
                <h1>Discord Activity Logger</h1>
            </div>
            
            <svg ref={svgRef}></svg>

            <select onClick={(e) => setSelected(e.target.value)}>
                <option value={"all"}>All</option>
                {users.map((item) => {
                    return (
                        <option value={item.username}>{item.username}</option>
                    )
                })}
            </select>

            <table>
                <thead>
                    <tr>
                        <td>Username</td>
                        <td>Status</td>
                        <td>Timestamp</td>
                    </tr>
                </thead>
                <tbody>
                    {selected === "all" ? 
                        data.map((item) => {
                            return (
                                <tr>
                                    <td><img width={50} height={50} src={item.avatar}></img>{item.username}</td>
                                    <td>{item.status}</td>
                                    <td>{new Date(item.timestamp).toLocaleString("cs")}</td>
                                </tr>
                            )
                        })
                        :
                        data.filter(user => user.username === selected).map((item) => {
                            return (
                                <tr>
                                    <td><img width={50} height={50} src={item.avatar}></img>{item.username}</td>
                                    <td>{item.status}</td>
                                    <td>{new Date(item.timestamp).toLocaleString("cs")}</td>
                                </tr>
                            )
                        })
                    }
                </tbody>
            </table>

            <div className='stats'>
                <p>
                    {/* Number of records: {stats.records}<br/>
                    Size of DB: {stats.size} MB */}
                </p>
            </div>
        </div>
    )
}

export default HomePage;
