import axios from "axios";
import { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import { Header } from "../components";

const UserPage = () => {
    const {user} = useParams();
    const [userData, setUserData] = useState(null);

    const fetchUser = async () => {
        const response = await axios.get("http://localhost:8080/api/general/user/" + user);
        return response.data;
    }

    useEffect(() => {
        fetchUser().then(data => setUserData(data))
    }, [])

    if (userData === null) return;

    return (
        <>
            <Header />

            <div className="container">
                <div className="user">
                    <div className="user-avatar">
                        <img src={userData[0].avatar}></img>
                    </div>
                    <div className="user-info">
                        <h2>{user}</h2>
                        <hr/>
                        <p>Total time: <span className='badge'>{(userData.filter((item) => item.status === "online" || item.status === "dnd" || item.status === "idle").length / 60).toFixed(1)} h</span></p>
                        <p>Last seen: {userData.filter((item) => item.status === "online" || item.status === "dnd" || item.status === "idle")[0].timestamp}</p>
                    </div>
                </div>
            </div>
            
        </>
    )
}

export default UserPage;
