import React from 'react';
import { ScatterChart, Scatter, XAxis, YAxis, ZAxis, Tooltip, Legend, ResponsiveContainer } from 'recharts';
import moment from 'moment';

const statusColors = {
  online: 'green',
  offline: 'grey',
  idle: 'orange',
  dnd: 'red',
};

const StatusChart = ({}) => {
  const chartData = [
    { username: "a", timestamp: 1, status: "online" },
    { username: "b", timestamp: 1, status: "online" },
    { username: "a", timestamp: 2, status: "online" },
    { username: "b", timestamp: 2, status: "online" },
    { username: "a", timestamp: 3, status: "online" },
    { username: "b", timestamp: 3, status: "online" },
  ];

  // Extract unique usernames and sort them
  const usernames = Array.from(new Set(chartData.map((entry) => entry.username))).sort();

  return (
    <ResponsiveContainer width='95%' height={100}>
      <ScatterChart>
        <XAxis dataKey='timestamp' domain={['auto', 'auto']} name='Time' type='number' />
        <YAxis dataKey='username' type='category' ticks={usernames} />
        <ZAxis type="category" dataKey="status" />
        <Tooltip cursor={{ strokeDasharray: '3 3' }} />

        {Object.keys(statusColors).map((status) => (
          <Scatter
            key={status}
            data={chartData.filter((entry) => entry.status === status).slice(0, 1000)}
            fill={statusColors[status]}
            shape="circle"
          />
        ))}
      </ScatterChart>
    </ResponsiveContainer>
  );
};

export default StatusChart;
