import React, { useEffect, useState } from 'react';
import { Scatter } from 'react-chartjs-2';

const getColor = (status) => {
    const statusColors = {
      online: 'green',
      offline: 'grey',
      idle: 'orange',
      dnd: 'red',
    };
  
    return statusColors[status] || 'black';
  };

const StatusChart = ({inputData}) => {
    const [data, setData] = useState(inputData)
  useEffect(() => {
    // Load your data here and transform it as needed
    // Example: fetch('your-data-endpoint').then(response => response.json()).then(data => setData(data));
    // Ensure to sort and format your data similarly to the original Python code

    // Example data structure:
    const exampleData = [
      { timestamp: '2023-01-01T12:00:00', username: 'user1', status: 'online' },
      // Add more data as needed
    ];

    // Transform the data to fit the Chart.js format
    const transformedData = {
      labels: Array.from(new Set(exampleData.map((entry) => entry.username))),
      datasets: exampleData.map((entry) => ({
        label: entry.username,
        data: [{ x: entry.timestamp, y: entry.username }],
        borderColor: getColor(entry.status),
        backgroundColor: getColor(entry.status),
        pointStyle: 'circle',
        pointRadius: 8,
        pointHoverRadius: 10,
      })),
    };

    setData(transformedData);
  }, []);

  return (
    <div>
      {data && (
        <Scatter
          data={data}
          options={{
            scales: {
              x: {
                type: 'time',
                time: {
                  unit: 'day',
                },
                title: {
                  display: true,
                  text: 'Timestamp',
                },
              },
              y: {
                title: {
                  display: true,
                  text: 'Username',
                },
              },
            },
            plugins: {
              legend: {
                position: 'top',
                labels: {
                  usePointStyle: true,
                },
              },
            },
          }}
        />
      )}
    </div>
  );
};

export default StatusChart;
