import { useEffect, useState } from "react";

const Chart = ({ data, users }) => {
  const [times, setTimes] = useState([]);
  const screenWidth = window.innerWidth; // Get the screen width

  const getLegend = () => {
    console.log(data);
  };

  useEffect(() => {
    let tmp = [];

    for (let i = new Date().getTime() - 86400000; i < new Date().getTime(); i += 60000) {
      tmp.push(i);
    }
    setTimes(tmp);
    getLegend();
  }, []);

  const calculateWidth = (startTime, endTime) => {
    const timeDiff = endTime - startTime;
    const maxWidth = screenWidth; // Maximum width based on screen width
    const percentage = (timeDiff / 86400000) * 100; // Convert time difference to percentage
    return (maxWidth * percentage) / 100; // Calculate width based on percentage
  };

  return (
    <>
      {users?.map((user) => {
        const userStatuses = data.filter((dataItem) => user.username === dataItem.username);
        return (
          <div
            key={user.username}
            style={{ display: "flex", flexDirection: "row", justifyContent: "space-evenly" }}
          >
            {userStatuses.map((item, index) => {
              const startTime = index === 0 ? item.timestamp : userStatuses[index - 1].timestamp;
              const endTime = item.timestamp;

              const width = calculateWidth(startTime, endTime);

              return (
                <div
                  key={item.timestamp}
                  style={{
                    backgroundColor: item.status === "active" ? "green" : "red",
                    minWidth: `${width}px`,
                    minHeight: "20px", // Set the height as per your requirement
                    margin: "5px",
                  }}
                ></div>
              );
            })}
          </div>
        );
      })}
    </>
  );
};

export default Chart;
