import { useEffect, useState, useRef } from 'react';
import axios from "axios";
import '../App.css'
import * as d3 from 'd3';

const HomePage = () => {
    const [data, setData] = useState([]);
    const [users, setUsers] = useState([]);
    const [stats, setStats] = useState(null);

    const [selected, setSelected] = useState("all");

    const fetchData = async () => {
        const response = await axios.get(`http://localhost:8080/api/general/`);
        return response.data;
    }

    const fetchUsers = async () => {
        const response = await axios.get(`http://localhost:8080/api/general/users`);
        return response.data;
    }

    const fetchStats = async () => {
        const response = await axios.get(`http://localhost:8080/api/general/stats`);
        return response.data;
    }

    const chartRef = useRef();

  useEffect(() => {
    // Convert timestamp to JavaScript Date object for better plotting
    data.forEach((d) => {
      d.timestamp = new Date(d.timestamp);
    });

    // Sort data by timestamp
    const sortedData = data.slice().sort((a, b) => a.timestamp - b.timestamp);

    // Define a color scale for the statuses
    const colorScale = d3
      .scaleOrdinal()
      .domain(['online', 'offline', 'idle', 'dnd'])
      .range(['green', 'grey', 'orange', 'red']);

    // Set up SVG dimensions
    const width = 800;
    const height = 400;

    // Create SVG container
    const svg = d3.select(chartRef.current).append('svg').attr('width', width).attr('height', height);

    // Create scatterplot
    svg
      .selectAll('circle')
      .data(sortedData)
      .enter()
      .append('circle')
      .attr('cx', (d) => d3.timeDay(d.timestamp) * 2) // Adjust x position as needed
      .attr('cy', (d) => d.username)
      .attr('r', 5) // Set circle radius
      .attr('fill', (d) => colorScale(d.status));

    // Add legend
    const legend = svg
      .selectAll('.legend')
      .data(colorScale.domain())
      .enter()
      .append('g')
      .attr('class', 'legend')
      .attr('transform', (d, i) => `translate(0,${i * 20})`);

    legend
      .append('rect')
      .attr('x', width - 18)
      .attr('width', 18)
      .attr('height', 18)
      .style('fill', colorScale);

    legend
      .append('text')
      .attr('x', width - 24)
      .attr('y', 9)
      .attr('dy', '.35em')
      .style('text-anchor', 'end')
      .text((d) => d);

    // Add axes and labels if needed

    // Cleanup on component unmount
    return () => {
      svg.selectAll('*').remove();
    };
  }, [data]);

    useEffect(() => {
        fetchData().then(response => setData(response))
        fetchUsers().then(response => setUsers(response))
        fetchStats().then(response => setStats(response))
    }, [])

    return (
        <div className='main'>
            
            <div className='header'>
                <img src={require("../img/discord.png")}></img>
                <h1>Discord Activity Logger</h1>
            </div>
            
            <div ref={chartRef}></div>

            <select onClick={(e) => setSelected(e.target.value)}>
                <option value={"all"}>All</option>
                {users.map((item) => {
                    return (
                        <option value={item.username}>{item.username}</option>
                    )
                })}
            </select>

            <table>
                <thead>
                    <tr>
                        <td>Username</td>
                        <td>Status</td>
                        <td>Timestamp</td>
                    </tr>
                </thead>
                <tbody>
                    {selected === "all" ? 
                        data.map((item) => {
                            return (
                                <tr>
                                    <td><img width={50} height={50} src={item.avatar}></img>{item.username}</td>
                                    <td>{item.status}</td>
                                    <td>{new Date(item.timestamp).toLocaleString("cs")}</td>
                                </tr>
                            )
                        })
                        :
                        data.filter(user => user.username === selected).map((item) => {
                            return (
                                <tr>
                                    <td><img width={50} height={50} src={item.avatar}></img>{item.username}</td>
                                    <td>{item.status}</td>
                                    <td>{new Date(item.timestamp).toLocaleString("cs")}</td>
                                </tr>
                            )
                        })
                    }
                </tbody>
            </table>

            <div className='stats'>
                <p>
                    {/* Number of records: {stats.records}<br/>
                    Size of DB: {stats.size} MB */}
                </p>
            </div>
        </div>
    )
}

export default HomePage;
