import React from 'react';
import { ScatterChart, Scatter, XAxis, YAxis, ZAxis, Tooltip, Legend } from 'recharts';
import moment from 'moment';  // Import moment for formatting timestamps

// Assuming presencesData is the data received from the API or some other source
// Make sure to convert timestamp to a JavaScript Date object

// Define a color palette for the statuses
const statusColors = {
  online: 'green',
  offline: 'grey',
  idle: 'orange',
  dnd: 'red',
};

const StatusChart = ({presencesData}) => {
  return (
    <ScatterChart
      width={800}
      height={400}
      margin={{ top: 20, right: 30, left: 20, bottom: 10 }}
    >
      <XAxis
        dataKey="timestamp"
        type="category"
        tickFormatter={(timestamp) => moment(timestamp).format('MM. DD. HH:mm')}
        angle={-45}
        textAnchor="end"
        height={60}
      />
      <YAxis dataKey="username" type="category" />
      <ZAxis type="category" dataKey="status" range={[0, 100]} />
      <Tooltip cursor={{ strokeDasharray: '3 3' }} />
      <Legend />
      {Object.keys(statusColors).map((status) => (
        <Scatter
          key={status}
          data={presencesData.filter((entry) => entry.status === status)[0]}
          fill={statusColors[status]}
          shape="circle"
        />
      ))}
    </ScatterChart>
  );
};

export default StatusChart;
