import React from 'react';
import { ScatterChart, Scatter, XAxis, YAxis, ZAxis, Tooltip, Legend } from 'recharts';
import moment from 'moment';

const statusColors = {
  online: 'green',
  offline: 'grey',
  idle: 'orange',
  dnd: 'red',
};

const StatusChart = ({presencesData}) => {

  const yDomain = presencesData.map((entry) => entry.username).sort();;

  return (
    <ScatterChart
      width={800}
      height={600} // Adjust the height to accommodate more items on the Y-axis
      margin={{ top: 20, right: 30, left: 20, bottom: 10 }}
    >
      <XAxis
        dataKey="timestamp"
        domain={['auto', 'auto']}
        name="Time"
        tickFormatter={(unixTime) => moment(unixTime).format('MM. DD. HH:mm')}
        type="number"
      />
      <YAxis dataKey="username" type="category" domain={yDomain} />
      <ZAxis type="category" dataKey="status" />
      <Tooltip cursor={{ strokeDasharray: '3 3' }} />

      {Object.keys(statusColors).map((status) => (
        <Scatter
          key={status}
          data={presencesData.filter((entry) => entry.status === status).slice(0, 100)}
          fill={statusColors[status]}
          shape="circle"
        />
      ))}
    </ScatterChart>
  );
};

export default StatusChart;
