import React from 'react';
import { ScatterChart, Scatter, XAxis, YAxis, ZAxis, Tooltip, Legend, ResponsiveContainer } from 'recharts';
import moment from 'moment';

const statusColors = {
  online: 'green',
  offline: 'grey',
  idle: 'orange',
  dnd: 'red',
};

const CustomTooltip = ({ active, payload, label }) => {
  if (active && payload && payload.length) {
    return (
      <div className="custom-tooltip">
        <p className="label">{`${label} : ${payload[0].value}`}</p>
        <p className="desc">Anything you want can be displayed here.</p>
      </div>
    );
  }

  return null;
};

const StatusChartUser = ({chartData}) => {
  return (
    <ResponsiveContainer width = '95%' height = {100} >
    <ScatterChart>
      <XAxis
        dataKey = 'timestamp'
        domain = {['auto', 'auto']}
        name = 'Time'
        tickFormatter = {(unixTime) => moment(unixTime).format('HH:mm Do')}
        type = 'number'
      />
      <YAxis dataKey = 'username' type='number'  />
      <ZAxis type="category" dataKey="status" />
      <Tooltip cursor={{ strokeDasharray: '3 3' }} />

      {Object.keys(statusColors).map((status) => (
        <Scatter
          key={status}
          data={chartData.filter((entry) => entry.status === status)}
          fill={statusColors[status]}
          shape="circle"
        />
      ))}

    </ScatterChart>
  </ResponsiveContainer>
  );
};

export default StatusChartUser;
