import React from 'react';
import { ScatterChart, Scatter, XAxis, YAxis, ZAxis, Tooltip, Legend } from 'recharts';
import moment from 'moment';

const statusColors = {
  online: 'green',
  offline: 'grey',
  idle: 'orange',
  dnd: 'red',
};

const StatusChart = () => {
  const presencesData = [
    { username: 'Honza', timestamp: 1, status: 'idle' },
    { username: 'Honza', timestamp: 2, status: 'online' },
    { username: 'Honza', timestamp: 3, status: 'dnd' },
    { username: 'Honza', timestamp: 4, status: 'offline' },
  ];

  // Sort data by timestamp
  const sortedData = presencesData.sort((a, b) => a.timestamp - b.timestamp);

  return (
    <ScatterChart
      width={800}
      height={400}
      margin={{ top: 20, right: 30, left: 20, bottom: 10 }}
    >
      <XAxis
        dataKey="timestamp"
        type="category"
        angle={-45}
        textAnchor="end"
        height={60}
      />
      <YAxis dataKey="username" type="category" />
      <ZAxis type="category" dataKey="status" />
      <Tooltip cursor={{ strokeDasharray: '3 3' }} />

      {Object.keys(statusColors).map((status) => (
        <Scatter
          key={status}
          data={sortedData.filter((entry) => entry.status === status).slice(0, 1000)}
          fill={statusColors[status]}
          shape="circle"
        />
      ))}
    </ScatterChart>
  );
};

export default StatusChart;
