import { useEffect, useState, useRef } from 'react';
import axios from "axios";
import '../App.css'
import * as d3 from 'd3';

const HomePage = () => {
    const [data, setData] = useState([]);
    const [users, setUsers] = useState([]);
    const [stats, setStats] = useState(null);

    const [selected, setSelected] = useState("all");

    const fetchData = async () => {
        const response = await axios.get(`http://localhost:8080/api/general/`);
        return response.data;
    }

    const fetchUsers = async () => {
        const response = await axios.get(`http://localhost:8080/api/general/users`);
        return response.data;
    }

    const fetchStats = async () => {
        const response = await axios.get(`http://localhost:8080/api/general/stats`);
        return response.data;
    }

    const svgRef = useRef();

  useEffect(() => {
    if (!data) return;

    // Convert timestamp to Date for better plotting
    data.forEach((d) => {
      d.timestamp = new Date(d.timestamp);
    });

    // Sort by timestamp
    data.sort((a, b) => a.timestamp - b.timestamp);

    // Define a color scale for the statuses
    const colorScale = d3
      .scaleOrdinal()
      .domain(['online', 'offline', 'idle', 'dnd'])
      .range(['green', 'grey', 'orange', 'red']);

    // Create SVG container
    const svg = d3.select(svgRef.current);

    // Create legend
    const legend = legendColor().scale(colorScale).title('Status');

    svg.append('g').attr('transform', 'translate(20,20)').call(legend);

    // Create scatter plot
    svg
      .selectAll('circle')
      .data(data)
      .enter()
      .append('circle')
      .attr('cx', (d) => xScale(d.timestamp))
      .attr('cy', (d) => yScale(d.username))
      .attr('r', 5)
      .attr('fill', (d) => colorScale(d.status));

  }, [data]);

    useEffect(() => {
        fetchData().then(response => setData(response))
        fetchUsers().then(response => setUsers(response))
        fetchStats().then(response => setStats(response))
    }, [])

    return (
        <div className='main'>
            
            <div className='header'>
                <img src={require("../img/discord.png")}></img>
                <h1>Discord Activity Logger</h1>
            </div>
            
            <svg ref={svgRef} width={800} height={400}>
      {/* Add any additional SVG elements or labels here */}
    </svg>

            <select onClick={(e) => setSelected(e.target.value)}>
                <option value={"all"}>All</option>
                {users.map((item) => {
                    return (
                        <option value={item.username}>{item.username}</option>
                    )
                })}
            </select>

            <table>
                <thead>
                    <tr>
                        <td>Username</td>
                        <td>Status</td>
                        <td>Timestamp</td>
                    </tr>
                </thead>
                <tbody>
                    {selected === "all" ? 
                        data.map((item) => {
                            return (
                                <tr>
                                    <td><img width={50} height={50} src={item.avatar}></img>{item.username}</td>
                                    <td>{item.status}</td>
                                    <td>{new Date(item.timestamp).toLocaleString("cs")}</td>
                                </tr>
                            )
                        })
                        :
                        data.filter(user => user.username === selected).map((item) => {
                            return (
                                <tr>
                                    <td><img width={50} height={50} src={item.avatar}></img>{item.username}</td>
                                    <td>{item.status}</td>
                                    <td>{new Date(item.timestamp).toLocaleString("cs")}</td>
                                </tr>
                            )
                        })
                    }
                </tbody>
            </table>

            <div className='stats'>
                <p>
                    {/* Number of records: {stats.records}<br/>
                    Size of DB: {stats.size} MB */}
                </p>
            </div>
        </div>
    )
}

export default HomePage;
