import React from 'react';
import { ScatterChart, Scatter, XAxis, YAxis, ZAxis, Tooltip, Legend, ResponsiveContainer } from 'recharts';
import moment from 'moment';

const statusColors = {
  online: 'green',
  offline: 'grey',
  idle: 'orange',
  dnd: 'red',
};

const StatusChart = ({chartData}) => {
  // const chartData = [
  //   { username: 15, timestamp: 1503617297689 },
  //   { username: 15, timestamp: 1503616962277 },
  //   { username: 15, timestamp: 1503616882654 },
  //   { username: 15, timestamp: 1503613184594 },
  //   { username: 15, timestamp: 1503611308914 },
  // ]
  console.log(chartData)
  return (
    <ResponsiveContainer width = '95%' height = {100} >
    <ScatterChart>
      <XAxis
        dataKey = 'timestamp'
        domain = {['auto', 'auto']}
        name = 'Time'
        tickFormatter = {(unixTime) => moment(unixTime).format('HH:mm Do')}
        type = 'number'
      />
      <YAxis dataKey = 'username' type='category'  domain={chartData.map((entry) => entry.username)}/>
      <ZAxis type="category" dataKey="status" />
      <Tooltip cursor={{ strokeDasharray: '3 3' }} />

      {Object.keys(statusColors).map((status) => (
        <Scatter
          key={status}
          data={chartData.filter((entry) => entry.status === status).slice(0, 1000)}
          fill={statusColors[status]}
          shape="circle"
        />
      ))}

    </ScatterChart>
  </ResponsiveContainer>
  );
};

export default StatusChart;
