import { useEffect, useState } from 'react';
import axios from "axios";
import '../App.css'

const HomePage = () => {
    const [data, setData] = useState([]);
    const [users, setUsers] = useState([]);

    const [selected, setSelected] = useState("all");

    const fetchData = async () => {
        const response = await axios.get(`http://localhost:8080/api/general/`);
        return response.data;
    }

    const fetchUsers = async () => {
        const response = await axios.get(`http://localhost:8080/api/general/users`);
        return response.data;
    }

    useEffect(() => {
        fetchData().then(response => setData(response))
        fetchUsers().then(response => setUsers(response))
    }, [])

    return (
        <>
            <select onClick={(e) => setSelected(e.target.value)}>
                <option value={"all"}>All</option>
                {users.map((item) => {
                    return (
                        <option value={item.username}>{item.username}</option>
                    )
                })}
            </select>

            <table>
                <thead>
                    <tr>
                        <td>Username</td>
                        <td>Status</td>
                        <td>Timestamp</td>
                    </tr>
                </thead>
                <tbody>
                    {selected === "all" ? 
                        data.map((item) => {
                            return (
                                <tr>
                                    <td>{item.username}</td>
                                    <td>{item.status}</td>
                                    <td>{new Date(item.timestamp).toLocaleString("cs")}</td>
                                </tr>
                            )
                        })
                        :
                        data.filter(user => user.username === selected).map((item) => {
                            return (
                                <tr>
                                    <td>{item.username}</td>
                                    <td>{item.status}</td>
                                    <td>{new Date(item.timestamp).toLocaleString()}</td>
                                </tr>
                            )
                        })
                    }
                </tbody>
            </table>
        </>
    )
}

export default HomePage;
