import { useEffect, useState, useRef } from 'react';
import axios from "axios";
import '../App.css'
import { Header, StatusChart } from '../components';

const HomePage = () => {
    const [data, setData] = useState([]);
    const [users, setUsers] = useState([]);
    const [stats, setStats] = useState(null);
    const [chartData, setChartData] = useState([]);
    const [selectedTimeSpan, setSelectedTimeSpan] = useState("day");

    const fetchData = async () => {
        const response = await axios.get(`http://localhost:8080/api/general/`);
        return response.data;
    }

    const fetchUsers = async () => {
        const response = await axios.get(`http://localhost:8080/api/general/users`);
        return response.data;
    }

    const fetchStats = async () => {
        const response = await axios.get(`http://localhost:8080/api/general/stats`);
        return response.data;
    }

   

    useEffect(() => {
        fetchUsers().then(response => {
            let result = []
            
            response.forEach((user) => {
                result.push({...user, timeOverall: (data.filter((item) => user.username === item.username && (item.status === "online" || item.status === "dnd" || item.status === "idle")).length / 60).toFixed(1)})
            })

            result.sort((a, b) => b.timeOverall - a.timeOverall)
            setUsers(result);
        })

        let obj = [];
        data.forEach((item) => {
            obj.push({username: item.username, status: item.status, timestamp: new Date(item.timestamp).getTime()})
        })
        setChartData(obj);
    }, [data])

    useEffect(() => {
        fetchData().then(response => setData(response))
        fetchStats().then(response => setStats(response))
    }, [])

    return (
        <div className='main'>
            
            <Header />
            
            {chartData.length != 0 && <StatusChart chartData={chartData} numOfUsers={users.length}/>}

            <select onChange={(e) => setSelectedTimeSpan(e.target.value)}>
                <option value={"hour"}>1H</option>
                <option value={"day"}>1D</option>
                <option value={"week"}>1W</option>
                <option value={"month"}>1M</option>
                <option value={"year"}>1Y</option>
            </select>

            <div className='card'>
                <h2>Users ({users.length})</h2>

                <div className='card-body'>
                    <ul>
                        {users.map((user) => {
                            return (<li><a href={`/u/${encodeURIComponent(user.username)}`}>{user.username}</a> <span className='badge'>{user.timeOverall} h</span></li>)
                        })}
                    </ul>
                </div>
            </div>

            <div className='stats'>
                <p>
                    Number of records: {stats?.records}<br/>
                    Size of DB: {stats?.size} MB
                </p>
            </div>
        </div>
    )
}

export default HomePage;
