import React from 'react';
import { ScatterChart, Scatter, XAxis, YAxis, ZAxis, Tooltip, Legend, ResponsiveContainer } from 'recharts';
import moment from 'moment';

const statusColors = {
  online: 'green',
  offline: 'grey',
  idle: 'orange',
  dnd: 'red',
};

const CustomTooltip = ({ active, payload, label }) => {
  if (active && payload && payload.length) {
    return (
      <div className="custom-tooltip">
        <p>{`Time: ${new Date(payload[0].value).toLocaleString("cs")}`}</p>
        <p>{`Username: ${payload[1].value}`}</p>
        <p>{`Status: ${payload[2].value}`}</p>
      </div>
    );
  }

  return null;
};

const StatusChart = ({chartData, numOfUsers}) => {
  // Create a mapping of usernames to numerical indices
  const usernameToIndex = chartData.reduce((acc, cur, index) => {
    acc[cur.username] = index%(chartData.length-numOfUsers);
    return acc;
  }, {});

  // Replace usernames in chartData with their corresponding indices
  const processedChartData = chartData.map(entry => ({
    ...entry,
    username: usernameToIndex[entry.username]
  }));


  return (
    <ResponsiveContainer width = '95%' height = {500} >
    <ScatterChart>
      <XAxis
        dataKey = 'timestamp'
        domain = {['auto', 'auto']}
        name = 'Time'
        type = 'number'
        tickFormatter = {(unixTime) => moment(unixTime).format('HH:mm Do')}
      />
      <YAxis 
        dataKey = 'username' 
        type='number' 
        ticks={Object.values(usernameToIndex)}
        tickFormatter={(index) => Object.keys(usernameToIndex).find(key => usernameToIndex[key] === index)} // Display actual usernames
        
      />
      <ZAxis type="category" dataKey="status" />
      <Tooltip content={<CustomTooltip />}  cursor={{ strokeDasharray: '3 3' }} />

      {Object.keys(statusColors).map((status) => (
        <Scatter
          key={status}
          data={processedChartData.filter((entry) => entry.status === status)}
          fill={statusColors[status]}
          shape="circle"
        />
      ))}
    </ScatterChart>
{console.log(Object.values(usernameToIndex), chartData.length)}

  </ResponsiveContainer>
  );
};

export default StatusChart;
