import React from 'react';
import { ScatterChart, Scatter, XAxis, YAxis, ZAxis, Tooltip, Legend, ResponsiveContainer } from 'recharts';
import moment from 'moment';

const statusColors = {
  online: 'green',
  offline: 'grey',
  idle: 'orange',
  dnd: 'red',
};

const StatusChart = ({}) => {
  const chartData = [
    { username: "a", timestamp: 1, status: "online" },
    { username: "b", timestamp: 1, status: "online" },
    { username: "a", timestamp: 2, status: "online" },
    { username: "b", timestamp: 2, status: "online" },
    { username: "a", timestamp: 3, status: "online" },
    { username: "b", timestamp: 3, status: "online" },
  ];

  // Create a mapping of usernames to numeric values
  const usernameToNumeric = {};
  chartData.forEach((entry, index) => {
    if (!usernameToNumeric[entry.username]) {
      usernameToNumeric[entry.username] = index;
    }
  });

  return (
    <ResponsiveContainer width='95%' height={100}>
      <ScatterChart>
        <XAxis
          dataKey='timestamp'
          domain={['auto', 'auto']}
          name='Time'
          type='number'
        />
        <YAxis dataKey='username' type='number' tickFormatter={(value) => chartData[value].username} />
        <ZAxis type="category" dataKey="status" />
        <Tooltip cursor={{ strokeDasharray: '3 3' }} />

        {Object.keys(statusColors).map((status) => (
          <Scatter
            key={status}
            data={chartData.filter((entry) => entry.status === status).map((entry) => ({
              ...entry,
              username: usernameToNumeric[entry.username],
            })).slice(0, 1000)}
            fill={statusColors[status]}
            shape="circle"
          />
        ))}

      </ScatterChart>
    </ResponsiveContainer>
  );
};

export default StatusChart;
